mapper.select:
SELECT * FROM ( SELECT a.YWXH, a.DWID, c.DWMC, c.dwdm, TO_CHAR(a.BBRQ, 'yyyy-MM-dd')BBRQ, a.JBR, TO_CHAR(a.BLRQ, 'yyyy-MM-dd HH24:mi:ss')BLRQ, TO_CHAR(b.SCRQ, 'yyyy-MM-dd')SCRQ, b.ZFRS,
a.tcfd,
a.FUYAN, a.FFDD, a.ZFQX
FROM GR_YLZF_YMX a LEFT JOIN YL_ZFYB b ON a.bbid = b.bbid and a.hzbz='1' LEFT JOIN DW_XX c ON a.DWID = c.DWID WHERE a.JBR <> 'sys'
and c.DWDM = #{dwdm,jdbcType=VARCHAR}
and a.ZFQX in
#{temp}
and a.BBRQ BETWEEN TO_DATE(#{hzdatefrom,jdbcType=VARCHAR}, 'yyyy-mm-dd') and TO_DATE(#{hzdateto,jdbcType=VARCHAR}, 'yyyy-mm-dd')
and a.BBRQ = TO_DATE(#{bbrq,jdbcType=VARCHAR}, 'yyyy-mm-dd')
and a.bzyy = #{bzyy,jdbcType=VARCHAR}
and a.HZBZ = #{ywbz,jdbcType=VARCHAR}
and b.CWBZ = #{cwbz,jdbcType=VARCHAR}
and b.ZFJE > #{zfje,jdbcType=VARCHAR}
and a.FUYAN = #{fuyan,jdbcType=VARCHAR}
and a.FFDD = #{ffdd,jdbcType=VARCHAR}
and b.SCRQ BETWEEN TO_DATE(#{ybdatefrom,jdbcType=VARCHAR}, 'yyyy-mm-dd') and TO_DATE(#{ybdateto,jdbcType=VARCHAR}, 'yyyy-mm-dd')
)
order by ${sortName} ${sortOrder}
order by SCRQ desc

SELECT a.GRID, a.DWID, e.jcid, c.DWMC, c.DWDM, d.xm grname, d.BZHM, a.FUYAN, a.bzyy, a.FFDD, a.JBR,
a.zfje, e.jcr, e.jcrq, e.jc, e.cxjc, e.jcjg, e.memo
FROM GR_YLZF_BZMX a LEFT JOIN YL_ZFYB b ON a.bbid = b.bbid and a.hzbz='1' LEFT JOIN DW_XX c ON a.DWID = c.DWID LEFT JOIN GR_XX d ON a.GRID = d.GRID LEFT JOIN JC_JG e ON a.GRID = e.GRID and e.mkdm = '1' WHERE a.JBR <> 'sys' and a.DWID = #{dwid,jdbcType=VARCHAR}

SELECT max(a.GRID) GRID,max(a.DWID) DWID,max(a.XM) GRNAME,max(a.BZHM) BZHM,max(a.BXH) BXH,max(a.GRSF) GRSF,max(b.FFDD) FFDD,max(c.DWMC) DWMC FROM GR_XX a LEFT JOIN GR_YLZF_YMX b ON a.GRID = b.GRID LEFT JOIN DW_XX c ON a.DWID = c.DWID WHERE b.JBR <> 'sys' AND a.GRID = #{grid,jdbcType=VARCHAR}
and a.DWID = #{dwid,jdbcType=VARCHAR}
GROUP BY a.GRID,a.DWID,a.XM ,a.BZHM,a.BXH,a.GRSF,b.FFDD,c.DWMC

SELECT
 sum(a.tcfd) tcfd
FROM
GR_YLZF_YMX a
LEFT JOIN YL_ZFYB b   ON a.bbid = b.bbid and a.hzbz='1'
LEFT JOIN DW_XX c 	ON a.DWID = c.DWID
WHERE a.JBR  <>  'sys'


    and c.DWDM = #{dwdm,jdbcType=VARCHAR}


    and a.ZFQX in

        #{temp}



    and a.BBRQ BETWEEN TO_DATE(#{hzdatefrom,jdbcType=VARCHAR}, 'yyyy-mm-dd') and TO_DATE(#{hzdateto,jdbcType=VARCHAR}, 'yyyy-mm-dd')


    and a.BBRQ = TO_DATE(#{bbrq,jdbcType=VARCHAR}, 'yyyy-mm-dd')


    and a.bzyy = #{bzyy,jdbcType=VARCHAR}


    and a.HZBZ = #{ywbz,jdbcType=VARCHAR}


    and b.CWBZ = #{cwbz,jdbcType=VARCHAR}


    and b.ZFJE > #{zfje,jdbcType=VARCHAR}


    and a.FUYAN = #{fuyan,jdbcType=VARCHAR}


    and a.FFDD = #{ffdd,jdbcType=VARCHAR}


    and b.SCRQ BETWEEN TO_DATE(#{ybdatefrom,jdbcType=VARCHAR}, 'yyyy-mm-dd') and TO_DATE(#{ybdateto,jdbcType=VARCHAR}, 'yyyy-mm-dd')

SELECT DWID,GRID
FROM(
    SELECT
        a.DWID,
        a.GRID
        FROM
        GR_YLZF_YMX a
        LEFT JOIN YL_ZFYB b ON a.bbid = b.bbid and a.hzbz='1'
        LEFT JOIN DW_XX c 	ON a.DWID = c.DWID
    WHERE
    a.JBR  <>  'sys'
    and  NOT EXISTS (SELECT * FROM JC_JG WHERE a.GRID=GRID AND MKDM='1')

        and c.DWDM = #{dwdm,jdbcType=VARCHAR}


        and a.ZFQX in

            #{temp}



        and a.BBRQ BETWEEN TO_DATE(#{hzdatefrom,jdbcType=VARCHAR}, 'yyyy-mm-dd') and TO_DATE(#{hzdateto,jdbcType=VARCHAR}, 'yyyy-mm-dd')


        and a.BBRQ = #{bbrq,jdbcType=VARCHAR}


        and a.bzyy = #{bzyy,jdbcType=VARCHAR}


        and a.HZBZ = #{ywbz,jdbcType=VARCHAR}


        and b.CWBZ = #{cwbz,jdbcType=VARCHAR}


        and b.ZFJE > #{zfje,jdbcType=VARCHAR}





        and a.FFDD = #{ffdd,jdbcType=VARCHAR}


        and b.SCRQ BETWEEN TO_DATE(#{ybdatefrom,jdbcType=VARCHAR}, 'yyyy-mm-dd') and TO_DATE(#{ybdateto,jdbcType=VARCHAR}, 'yyyy-mm-dd')


        and c.dwid in

            #{id}


)
GROUP BY GRID,DWID
